mapper.update:
update board
set title = #{title}
,writer = #{writer}
,content=#{content}
,upd_date=now()
where board_no = #{boardNo}

mapper.select:
select @rownum := @rownum+1 row_no, b.* from (select @rownum:= #{startRowIndex} from dual) tmp, board b left join (select board_no, count(*) reply_count from board_reply group by board_no) r on b.board_no = r.board_no;
where title like concat('%',#{keyword},'%') or writer like concat('%',#{keyword},'%') or content like concat('%',#{keyword},'%')
order by group_no desc , seq_no ASC , depth_no ASC , reg_date DESC limit #{startRowIndex}, #{rowsPerPage}

mapper.insert:

	SELECT MAX(board_no) from board;



insert into board_attach (full_name,file_name, board_no)
values(#{fullName},#{fileName},#{boardNo})

mapper.select:
select *
from board_attach
where board_no = #{boardNo}

mapper.update:
